import groovy.json.JsonSlurper
def response = context.testCase.getTestStepByName("ValidateStatusCode").getPropertyValue("response")
def holder = new JsonSlurper().parseText(response)
def at = holder.data.access_token
log.info at
context.testCase.setPropertyValue("access_token","bearer "+at) context.testCase.testSuite.setPropertyValue("access_token","bearer "+at) context.testCase.testSuite.project.setPropertyValue("access_token","bearer "+at)

