import jxl.* 
//i am working woth Excel write //first thing is the path  
filepath = "C:\\Users\\AmritaChowdhury\\Documents\\Personal\\FullStackTester_training\\TestDataRest.xls" 
def accessToken = "0a4e198c754cf6e07c300d44b035044e0f32c810"; 
File excel = new File(filepath) //excel contains two thing one it contains workbook//sheet 
Workbook wb = Workbook.getWorkbook(excel) //i have to get the sheet 
Sheet sh = wb.getSheet("Sheet1")
def row = sh.getRows().toInteger()
def columns =  sh.getColumns().toInteger()

log.info "The Number of Rows are  $row" 
log.info "The Number of Columns are $columns" 

for(i=1;i<row;i++) 
{     Cell c = sh.getCell(0,i)     
log.info ("Ths id is " +c.getContents());     
def get = new URL("http://rest-api.upskills.in/api/rest_admin/customers/"+c.getContents()).openConnection();     
get.setRequestProperty("Authorization","bearer "+accessToken)     
def getRC = get.getResponseCode();     
log.info "The Response code is "+ getRC 
}

